import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { CheckCircle, XCircle, AlertCircle, Download, Share } from "lucide-react";

interface AnalysisResultsProps {
  analysis: {
    analysis: {
      decision: string;
      approvedAmount: string;
      justification: string;
    };
    topClauses: Array<{
      id: string;
      title: string;
      text: string;
      page_number: number;
    }>;
    query: string;
  };
}

export default function AnalysisResults({ analysis }: AnalysisResultsProps) {
  const { analysis: result, topClauses, query } = analysis;

  const getDecisionIcon = (decision: string) => {
    switch (decision.toLowerCase()) {
      case 'yes':
        return <CheckCircle className="w-6 h-6 text-success" />;
      case 'no':
        return <XCircle className="w-6 h-6 text-error" />;
      case 'partial':
        return <AlertCircle className="w-6 h-6 text-warning" />;
      default:
        return <AlertCircle className="w-6 h-6 text-neutral-500" />;
    }
  };

  const getDecisionColor = (decision: string) => {
    switch (decision.toLowerCase()) {
      case 'yes':
        return 'success';
      case 'no':
        return 'error';
      case 'partial':
        return 'warning';
      default:
        return 'neutral-500';
    }
  };

  const getDecisionText = (decision: string) => {
    switch (decision.toLowerCase()) {
      case 'yes':
        return 'Claim Approved';
      case 'no':
        return 'Claim Rejected';
      case 'partial':
        return 'Partial Approval';
      default:
        return 'Analysis Complete';
    }
  };

  return (
    <Card className="bg-white shadow-card">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-neutral-800">Coverage Analysis Results</h2>
          <div className="flex items-center space-x-2">
            <Button variant="ghost" size="sm" className="p-2 text-neutral-500 hover:text-primary">
              <Download className="w-4 h-4" />
            </Button>
            <Button variant="ghost" size="sm" className="p-2 text-neutral-500 hover:text-primary">
              <Share className="w-4 h-4" />
            </Button>
          </div>
        </div>

        {/* Decision Summary */}
        <div className={`bg-${getDecisionColor(result.decision)} bg-opacity-10 border border-${getDecisionColor(result.decision)} border-opacity-20 rounded-lg p-4 mb-6`}>
          <div className="flex items-start space-x-3">
            <div className="flex-shrink-0 mt-0.5">
              {getDecisionIcon(result.decision)}
            </div>
            <div className="flex-1">
              <h3 className={`font-semibold text-${getDecisionColor(result.decision)} mb-1`}>
                {getDecisionText(result.decision)}
              </h3>
              <p className="text-sm text-neutral-700">
                Approved Amount: {result.approvedAmount}
              </p>
            </div>
          </div>
        </div>

        {/* Query Summary */}
        <div className="mb-6">
          <h4 className="font-medium text-neutral-800 mb-2">Query Analyzed</h4>
          <div className="bg-neutral-50 rounded-lg p-3">
            <p className="text-sm text-neutral-700">{query}</p>
          </div>
        </div>

        {/* Detailed Justification */}
        <div className="space-y-4 mb-6">
          <h4 className="font-medium text-neutral-800">Analysis Justification</h4>
          <div className="bg-neutral-50 rounded-lg p-4">
            <p className="text-sm text-neutral-700 leading-relaxed">
              {result.justification}
            </p>
          </div>
        </div>

        {/* Relevant Policy Clauses */}
        {topClauses && topClauses.length > 0 && (
          <div>
            <h4 className="font-medium text-neutral-800 mb-4">Relevant Policy Clauses</h4>
            <div className="space-y-3">
              {topClauses.map((clause) => (
                <div key={clause.id} className="border border-neutral-200 rounded-lg p-4">
                  <div className="flex items-start justify-between mb-2">
                    <h5 className="font-medium text-sm text-neutral-800">{clause.title}</h5>
                    <span className="text-xs text-neutral-500 bg-neutral-100 px-2 py-1 rounded">
                      Page {clause.page_number}
                    </span>
                  </div>
                  <p className="text-sm text-neutral-600">
                    {clause.text.length > 200 
                      ? `${clause.text.substring(0, 200)}...` 
                      : clause.text
                    }
                  </p>
                </div>
              ))}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/ClaimsForm.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Search, AlertCircle } from "lucide-react";

const queryFormSchema = z.object({
  query: z.string().min(10, "Please provide a detailed query about your insurance claim"),
  documentId: z.string().min(1, "Please select a document first"),
});

type QueryFormData = z.infer<typeof queryFormSchema>;

interface ClaimsFormProps {
  selectedDocumentId: string | null;
  onAnalysisComplete: (analysis: any) => void;
}

export default function ClaimsForm({ selectedDocumentId, onAnalysisComplete }: ClaimsFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch,
  } = useForm<QueryFormData>({
    resolver: zodResolver(queryFormSchema),
    defaultValues: {
      query: '',
      documentId: selectedDocumentId || '',
    },
  });

  // Update form when document is selected
  useEffect(() => {
    if (selectedDocumentId) {
      setValue('documentId', selectedDocumentId);
    }
  }, [selectedDocumentId, setValue]);

  const analyzeClaimMutation = useMutation({
    mutationFn: async (data: { query: string; documentId: string }) => {
      // Parse the natural language query to extract information
      const queryText = data.query.toLowerCase();

      // Extract age using regex patterns
      const ageMatch = queryText.match(/(\d+)[-\s]?(?:year|yr|y)[-\s]?old|(\d+)m|(\d+)f|age[:\s]*(\d+)|(\d+)[,\s]+(?:male|female)/);
      const extractedAge = ageMatch ? parseInt(ageMatch[1] || ageMatch[2] || ageMatch[3] || ageMatch[4] || ageMatch[5]) : null;

      // Extract gender
      let extractedGender = null;
      if (queryText.includes('male') && !queryText.includes('female')) {
        extractedGender = 'male';
      } else if (queryText.includes('female')) {
        extractedGender = 'female';
      } else if (queryText.match(/\d+m[\s,]/)) {
        extractedGender = 'male';
      } else if (queryText.match(/\d+f[\s,]/)) {
        extractedGender = 'female';
      }

      // Extract amount
      const amountMatch = queryText.match(/(?:rs\.?||inr)[\s]*([0-9,]+)|([0-9,]+)[\s]*(?:rs\.?||inr)/);
      const extractedAmount = amountMatch ? parseInt((amountMatch[1] || amountMatch[2]).replace(/,/g, '')) : null;

      // Create claim with extracted or minimal data
      const claimData = {
        documentId: data.documentId,
        patientAge: extractedAge,
        gender: extractedGender,
        procedure: data.query,
        location: null,
        distance: null,
        policyDuration: null,
        claimAmount: extractedAmount,
        reimbursementPercentage: 100,
      };

      const claimResponse = await fetch('/api/claims', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(claimData),
      });
      if (!claimResponse.ok) {
        const error = await claimResponse.json();
        throw new Error(error.message || 'Failed to create claim');
      }
      const claim = await claimResponse.json();

      // Then analyze the claim
      const analysisResponse = await fetch(`/api/claims/${claim.id}/analyze`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({}),
      });
      if (!analysisResponse.ok) {
        const error = await analysisResponse.json();
        throw new Error(error.message || 'Failed to analyze claim');
      }
      return analysisResponse.json();
    },
    onSuccess: (data) => {
      onAnalysisComplete(data);
      toast({
        title: "Analysis Complete",
        description: "Claim analysis has been completed successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/analyses'] });
    },
    onError: (error: any) => {
      toast({
        title: "Analysis Failed",
        description: error.message || "Failed to analyze claim.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = async (data: QueryFormData) => {
    if (!selectedDocumentId) {
      toast({
        title: "No Document Selected",
        description: "Please upload a policy document first.",
        variant: "destructive",
      });
      return;
    }

    try {
      await analyzeClaimMutation.mutateAsync(data);
    } catch (error) {
      console.error("Form submission error:", error);
    }
  };

  const isLoading = analyzeClaimMutation.isPending;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200">
      <div className="p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Submit Your Query</h2>

        {!selectedDocumentId && (
          <div className="mb-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg flex items-start space-x-3">
            <AlertCircle className="w-5 h-5 text-yellow-600 mt-0.5 flex-shrink-0" />
            <p className="text-sm text-yellow-800">
              Please upload and process policy documents first. The system can only evaluate claims based on information found in uploaded documents.
            </p>
          </div>
        )}

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <div>
            <label htmlFor="query" className="block text-sm font-medium text-gray-700 mb-2">
              Natural Language Query
            </label>
            <Textarea
              id="query"
              placeholder="e.g., I need coverage for heart surgery for a 45-year-old patient in Mumbai with a 2-year-old policy"
              rows={4}
              {...register("query")}
              className="w-full resize-none"
              disabled={!selectedDocumentId}
            />
            {errors.query && (
              <p className="text-sm text-red-600 mt-1">{errors.query.message}</p>
            )}
          </div>

          <Button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-4 rounded-lg font-medium transition-colors"
            disabled={isLoading || !selectedDocumentId}
          >
            <Search className="w-5 h-5 mr-2" />
            {isLoading ? 'Evaluating Claim from Documents...' : 'Evaluate Claim from Documents'}
          </Button>
        </form>
      </div>
    </div>
  );
}

